import React, { useState, useRef, useEffect } from 'react';
import styled from 'styled-components';
import { motion, AnimatePresence, useAnimation } from 'framer-motion';
import { 
  NeuHeading, 
  NeuText, 
  NeuPrimaryButton,
  NeuAnimatedContainer,
  NeuCard,
  NeuButton,
  NeuCircle,
  NeuDivider
} from '../components/NeumorphicElements';
import { useAppContext } from '../context/AppContext';
import { getCardPhotos, Photo } from '../utils/photos';
import theme from '../styles/GlobalStyles';

// Album styled components
const CardInsideContainer = styled(NeuAnimatedContainer)`
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  transform-style: preserve-3d;
  perspective: 1500px;
  box-shadow: 0 15px 25px rgba(0, 0, 0, 0.1);
  padding: 0;
  overflow: visible;
  position: relative;
  z-index: 1;

  /* Ensure content is scrollable on mobile */
  max-height: 100%;

  @media (max-width: ${theme.breakpoints.mobile}) {
    overflow-y: auto;
    height: 100vh;
    padding-bottom: 20px;
  }
`;

const CardInsideContent = styled(motion.div)`
  width: 100%;
  padding: ${theme.spacing.lg};
  transform-style: preserve-3d;
  min-height: 600px;
  border-radius: ${theme.borderRadius.medium};
  box-shadow: 5px 5px 20px ${theme.colors.shadow1}, 
              -5px -5px 20px ${theme.colors.shadow2};
  background: linear-gradient(135deg, #f0f3f8, ${theme.colors.background});
  position: relative;
  background-image: url("data:image/svg+xml,%3Csvg width='60' height='60' viewBox='0 0 60 60' xmlns='http://www.w3.org/2000/svg'%3E%3Cg fill='none' fill-rule='evenodd'%3E%3Cg fill='%239C92AC' fill-opacity='0.05'%3E%3Cpath d='M36 34v-4h-2v4h-4v2h4v4h2v-4h4v-2h-4zm0-30V0h-2v4h-4v2h4v4h2V6h4V4h-4zM6 34v-4H4v4H0v2h4v4h2v-4h4v-2H6zM6 4V0H4v4H0v2h4v4h2V6h4V4H6z'/%3E%3C/g%3E%3C/g%3E%3C/svg%3E");

  @media (max-width: ${theme.breakpoints.mobile}) {
    min-height: auto;
    overflow: visible;
  }
`;

const CardEdge = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 20px;
  height: 100%;
  background: linear-gradient(to right, 
    rgba(0,0,0,0.05),
    rgba(0,0,0,0.02) 20%,
    rgba(0,0,0,0) 80%
  );
  transform-origin: left center;
  transform: rotateY(-90deg) translateX(-10px);
  z-index: 2;
  border-radius: 5px 0 0 5px;
`;

const CardInsideTitle = styled(NeuHeading)`
  text-align: center;
  font-size: 2rem;
  color: ${theme.colors.primary};
  margin-bottom: ${theme.spacing.lg};
  transform: translateZ(15px);

  @media (max-width: ${theme.breakpoints.mobile}) {
    font-size: 1.5rem;
  }
`;

const AlbumGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: ${theme.spacing.lg};
  margin-top: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.md};
  position: relative;

  @media (max-width: ${theme.breakpoints.mobile}) {
    grid-template-columns: repeat(auto-fill, minmax(140px, 1fr));
    gap: ${theme.spacing.md};
    margin-bottom: ${theme.spacing.xl}; /* Add more space at the bottom for mobile */
  }
`;

const PhotoFrame = styled(motion.div)`
  position: relative;
  padding: 10px;
  background-color: white;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  transition: all 0.3s ease;
  cursor: pointer;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: 1px solid rgba(0, 0, 0, 0.1);
    pointer-events: none;
